import { useState } from "react";

const Form = () => {
  const [Firstname, setFirstname] = useState("");
  const [Lastname, setLastname] = useState("");
  const [Begindate, setBegindate] = useState("");
  const [Enddate, setEnddate] = useState("");
  const [Hours, setHours] = useState("");
  const [Message, setMessage] = useState("");
  const [errors, setErrors] = useState({});
  const [buttonText, setButtonText] = useState("Submit");
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);
  const [showFailureMessage, setShowFailureMessage] = useState(false);


  const handleValidation = () => {
    let tempErrors = {};
    let isValid = true;

    if (Firstname.length <= 0) {
      tempErrors["Firstname"] = true;
      isValid = false;
    }
    if (Lastname.length <= 0) {
      tempErrors["Lastname"] = true;
      isValid = false;
    }
    if (!isNaN(Begindate)) {
      tempErrors["Begindate"] = true;
      isValid = false;
    }
    if (!isNaN(Enddate)) {
      tempErrors["Enddate"] = true;
      isValid = false;
    }
    if (Hours.length <= 0) {
      tempErrors["Hours"] = true;
      isValid = false;
    }

    if (Message.length <= 0) {
      tempErrors["Message"] = true;
      isValid = false;
    }

    setErrors({ ...tempErrors });
    console.log("errors", errors);
    return isValid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let isValidForm = handleValidation();
    console.log(isValidForm);

    // trim begin date and end date
    setBegindate(trimer(Begindate));
    setEnddate(trimer(Enddate));

    const trimer = (string) =>{
      let ans = string.replace('T', ' ');
      return ans;
    }
    

    if (isValidForm) {
      const id = 0;
      setButtonText("Sending");
      const res = await fetch("https://localhost:44327/Submit/add/records", {
        body: JSON.stringify({
          id: id,
          firstname: Firstname,
          lastname: Lastname,
          begindate: Begindate,
          enddate: Enddate,
          hours: Hours,
          message: Message,
        }),
        headers: {
          "Content-Type": "application/json",
        },
        method: "POST",
      });

      const { error } = await res.json();
      if (error) {
        console.log(error);
        setShowSuccessMessage(false);
        setShowFailureMessage(true);
        setButtonText("Sent");

        setFirstname("");
        setLastname("");
        setBegindate("");
        setEnddate("");
        setHours("");
        setMessage("");
        return;
      }
      setShowSuccessMessage(true);
      setShowFailureMessage(false);
      setButtonText("Sent");

    

      setFirstname("");
      setLastname("");
      setBegindate("");
      setEnddate("");
      setHours("");
      setMessage("");
      setButtonText("send");

      console.log(Firstname, Lastname, Begindate, Enddate, Hours, Message);
    }
  };
  return (
    <div className="flex justify-center">
      <form className="w-full max-w-lg content-center" onSubmit={handleSubmit}>
      <h1 className="text-5xl font-bold mt-0 mb-6 text-center">PTO Submission</h1>
      <div className="flex flex-wrap -mx-3 mb-6 ">
        <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-first-name">
            First Name
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
            id="grid-first-name"
            type="text"
            placeholder="Jane"
            onChange={(e)=> setFirstname(e.target.value)}
          />
          {errors?.Firstname && <p className="text-red-500 text-xs italic">
            Please fill out this field.
          </p> }
        </div>
        <div className="w-full md:w-1/2 px-3">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-last-name"
          >
            Last Name
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
            id="grid-last-name"
            type="text"
            placeholder="Doe"
            onChange={(e)=> setLastname(e.target.value)}
          />
            {errors?.Lastname && <p className="text-red-500 text-xs italic">
            Please fill out this field.
          </p> }
        </div>
      </div>
      <div className="flex flex-wrap -mx-3 mb-6 ">
        <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-start-time"
          >
            begin date
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
            id="grid-start-time"
            type="datetime-local"
            placeholder="Jane"
            onChange={(e)=> setBegindate(e.target.value)}
          />
          {errors?.Begindate && <p className="text-red-500 text-xs italic">
            Please fill out this field.
          </p> }
        </div>
        <div className="w-full md:w-1/2 px-3">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-end-time"
          >
            End date
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
            id="grid-end-time"
            type="datetime-local"
            onChange={(e)=> setEnddate(e.target.value)}
          />
        </div>
      </div>

      <div className="flex flex-wrap -mx-3 mb-6">
        <div className="w-full px-3">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-password"
          >
            Total hours
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
            id="Hours"
            type="number"
            onChange={(e)=> setHours(e.target.value)}
          />
          {errors?.Hours && <p className="text-red-500 text-xs italic">
            Please fill out this field.
          </p> }
          <p className="text-gray-600 text-xs italic">
            Your total vocation hours 
          </p>
        </div>
      </div>
      <div className="flex flex-wrap -mx-3 mb-6">
        <div className="w-full px-3">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-password"
          >
            Reason
          </label>
          <textarea
            className=" no-resize appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500 h-48 resize-none"
            id="message"
            onChange={(e)=> setMessage(e.target.value)}
          ></textarea>
          <p className="text-gray-600 text-xs italic">
            Please briefy write down the reason 
          </p>
        </div>
      </div>
      <div className="md:flex md:items-center">
        <div className="md:w-1/3">
          <button className="shadow bg-teal-400 hover:bg-teal-400 focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded">
            {buttonText}
          </button>
          {showSuccessMessage && <p className="text-grey-500 text-xs italic">
            Submiited.
          </p> }
          {showFailureMessage && <p className="text-red-500 text-xs italic">
            Please try it again !
          </p> }
        </div>
        <div className="md:w-2/3"></div>
      </div>
    </form>
      </div>
  );
};

export default Form;
