import Calendar from "../../Component/Calendar/Calendar";
import { getAllrecords } from "../../Service/UserService";
import { useEffect, useState } from "react";

const Calview = () =>{
    const [data, setData] = useState([]);

    useEffect(()=>{
        getAllrecords()
          .then(records => {
            setData(records);
          })
    },[]);

    const events = [];

    data.forEach(element => {
        Object.assign(events, {title : element.message, start: element.begindate, end: element.enddate
        })
    });

    console.log(events);

    return (
        <div className="container max-auto px-5 ">
            <Calendar {...data}/>
        </div>
    )
}


export default Calview;
