import Calendar from "../../Component/Calendar/Calendar";
import { getAllrecords } from "../../Service/UserService";
import { useEffect, useState } from "react";

const Calview = () =>{
    const [data, setData] = useState([]);

    useEffect(()=>{
        getAllrecords()
          .then(records => {
            setData(records);
          })
    },[]);

    const events = [{ title: 'Meeting', start: new Date() }];

    for(var i=0; i< data.length; i++){
         events[i] = {title:data[i].message, start:data[i].begindate, end:data[i].enddate
        }
    }

    console.log(events)


    return (
        <div className="container max-auto px-5 ">
            <Calendar {...events}/>
        </div>
    )
}


export default Calview;
