import { useMemo } from "react";
import { getAllrecords } from "../../Service/UserService";
import Table from "../../Component/Table/Table"


const Recordview = () =>{
    const columns = useMemo(() => [
        {
          Header: "id",
          accessor: 'id',
        },
        {
          Header: "First Name",
          accessor: 'firstname',
        },
        {
          Header: "Last Name",
          accessor: 'lastname',
        },
        {
          Header: "Begin Date",
          accessor: 'begindate',
        },
        {
          Header: "End Date",
          accessor: 'endate',
        },
        {
            Header: "Reason",
            accessor: 'message',
          }
      ], [])

    const record = [];
    var index =0;

    function breakdown(item) {
        record[index] = {item};
        index++;
    }

    getAllrecords()
          .then(users => {
            users.map(breakdown)
          })
    
    // const retrieve = () => record;
    console.log(record);

    const getData = () => {
        const data = record
        return [...data, ...data, ...data]
      }   

    const data = useMemo(()=> getData(), []);


    return(
        <div className="mt-5">
            <h2 className="text-center text-xl">Time off Chart</h2>
            <Table columns={columns} data={data} />
        </div>
    )

    

}
export default Recordview;
