import { useState } from "react";

export const Users = async () =>{

    const res = await fetch("https://localhost:44327/Submit/get");

    const [users, SetUsers] = useState([]);

    SetUsers(await res.json());

    if(users.length === 0)return( <p>There is no user data</p>)

    const UserRow = (user, index) => {
        return (
            <tr key = {index}>
            <td>{index+1}</td>
            <td>{user.firstname}</td>
            <td>{user.lastname}</td>
            <td>{user.begindate}</td>
            <td>{user.enddate}</td>
            <td>{user.hours}</td>
            </tr>
        )
    }

    const userTable = users.map((user,index) => UserRow(user,index))

    return(
        <div className="container">
            <h2>Users</h2>
            <table className="table table-bordered">
                <thead>
                <tr>
                    <th>User Id</th>
                    <th>Firstname</th>
                    <th>Lastname</th>
                    <th>begin date</th>
                    <th>end date</th>
                    <th>hours</th>
                </tr>
                </thead>
                <tbody>
                    {userTable}
                </tbody>
            </table>
        </div>
    )
}
