const Form = () =>{
  const [Firstname,setFirstname] = useState("");
      const [Lastname,setLastname] = useState("");
      const[Begindate, setBegindate] = useState("");
      const[Enddate, setEnddate] = useState("");
      const [Email, setEmail] = useState("");
      const [Message, setMessage] = useState("");
      const [errors, setErrors] = useState({});
      const [buttonText, setButtonText] = useState("send message");
      const [showSuccessMessage, setShowSuccessMessage] = useState(false);
      const [showFailureMessage, setShowFailureMessage] = useState(false);

      const handleValidation = () =>{
        let tempErrors = {};
        let isValid = true;

        if(Firstname.length <= 0){
          tempErrors["Firstname"] = true;
          isValid = false;
        }
        if(Lastname.length <=0){
          tempErrors["Lastname"] = true;
          isValid = false;
        }
        if(!isNaN(Begindate)){
          tempErrors["Begindate"] = true;
          isValid = false;
        }
        if(!isNaN(Enddate)){
            tempErrors["Enddate"] = true;
            isValid = false;
          }
        if(Email.length <=0){
          tempErrors["Email"] = true;
          isValid = false;
        }
        
        if(Message.length <=0){
          tempErrors["Message"] = true;
          isValid = false;
        }

        setErrors({ ...tempErrors });
        console.log("errors", errors);
        return isValid;

        }

      const handleSubmit = async (e) =>{
        e.preventDefault();
        
        let isValidForm = handleValidation();
        console.log(isValidForm);
        
        if(isValidForm){
          setButtonText("Sending");
          const res = await fetch("/api/sendgrid", {
            body: JSON.stringify({
              Firstname: Firstname,
              Lastname: Lastname,
              Email: Email,
              Begindate: Begindate,
              Enddate: Enddate,
              Message: Message,
            }),
            headers: {
              "Content-Type": "application/json",
            },
            method: "POST",
          });

        const {error} = await res.json();
        if(error){
          console.log(error);
          setShowSuccessMessage(false);
          setShowFailureMessage(true);
          setButtonText("Sent");

        setFirstname("");
        setLastname("");
        setBegindate("");
        setEnddate("");
        setEmail("");
        setMessage("");
        return;
        }
        setShowSuccessMessage(true);
        setShowFailureMessage(false);
        setButtonText("Sent");

        setFirstname("");
        setLastname("");
        setBegindate("");
        setEnddate("");
        setEmail("");
        setMessage("");
        console.log(Firstname,Lastname,Begindate,Enddate,Email, Message);
        }
      }
    return(
        <form className="w-full max-w-lg content-center" onSubmit={handleSubmit}>
      <div className="flex flex-wrap -mx-3 mb-6 ">
        <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-first-name"
          >
            First Name
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
            id="grid-first-name"
            type="text"
            placeholder="Jane"
          />
          <p className="text-red-500 text-xs italic">
            Please fill out this field.
          </p>
        </div>
        <div className="w-full md:w-1/2 px-3">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-last-name"
          >
            Last Name
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
            id="grid-last-name"
            type="text"
            placeholder="Doe"
          />
        </div>
      </div>
      <div className="flex flex-wrap -mx-3 mb-6 ">
        <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-start-time"
          >
            begin
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
            id="grid-start-time"
            type="datetime-local"
            placeholder="Jane"
          />
          <p className="text-red-500 text-xs italic">
            Please fill out this field.
          </p>
        </div>
        <div className="w-full md:w-1/2 px-3">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-end-time"
          >
            End
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
            id="grid-end-time"
            type="datetime-local"
          />
        </div>
      </div>
      
      <div className="flex flex-wrap -mx-3 mb-6">
        <div className="w-full px-3">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-password"
          >
            E-mail
          </label>
          <input
            className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
            id="email"
            type="email"
          />
          <p className="text-gray-600 text-xs italic">
            Some tips - as long as needed
          </p>
        </div>
      </div>
      <div className="flex flex-wrap -mx-3 mb-6">
        <div className="w-full px-3">
          <label
            className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
            for="grid-password"
          >
            Reason
          </label>
          <textarea
            className=" no-resize appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500 h-48 resize-none"
            id="message"
          ></textarea>
          <p className="text-gray-600 text-xs italic">
            Re-size can be disabled by set by resize-none / resize-y / resize-x
            / resize
          </p>
        </div>
      </div>
      <div className="md:flex md:items-center">
        <div className="md:w-1/3">
          <button
            className="shadow bg-teal-400 hover:bg-teal-400 focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded"
            type="button"
          >
            Send
          </button>
        </div>
        <div className="md:w-2/3"></div>
      </div>
    </form>
    )
}


export default Form;
