import { useMemo } from "react";
import { getAllrecords } from "../../Service/UserService";
import Table from "../../Component/Table/Table"


const Recordview = () =>{
    const columns = useMemo(
        () =>[
            {
                Header: "id",
                accessor: "id",
            },
            {
                Header: "firstname",
                accessor: "firstname",
            },
            {
                Header: "lastname",
                accessor: "lastname",
            },
            {
                Header: "begin date",
                accessor: "begin date",
            },
            {
                Header: "end date",
                accessor: "end date",
            },
            {
                Header: "hours",
                accessor: "hours",
            },
            {
                Header: "message",
                accessor: "message",
            }
        ]
    );

    const [state,useState] = useState([]);

    getAllrecords(this.state.user)
    .then(users => {
        console.log(users)
        useState({users: users})
      });

    

    const records = () => [state];


    const data = useMemo(()=> records,[]);

    return(
        <div className="mt-5">
            <h2 className="text-center text-xl">Time off Chart</h2>
            <Table columns={columns} data={data} />
        </div>
    )

    

}
export default Recordview;
