import { useState } from "react";
import { getAlluser } from "../../Service/UserService";


export const Users = () =>{
    const [state,SetState] = useState([]);

        getAlluser().then(users => {
            console.log(users)
            SetState(users);
          });


    if(!state){return null}
    const userTable =  state.map(users => {
            return (
                <tr>
                <td>{users.firstname}</td>
                <td>{users.lastname}</td>
                <td>{users.begindate}</td>
                <td>{users.enddate}</td>
                <td>{users.hours}</td>
                </tr>
            )
    }
    )

    return(
        <div className="container">
            <h2>Users</h2>
            <table className="table table-bordered">
                <thead>
                <tr>
                    <th>Firstname</th>
                    <th>Lastname</th>
                    <th>begin date</th>
                    <th>end date</th>
                    <th>hours</th>
                </tr>
                </thead>
                <tbody>
                    {userTable}
                </tbody>
            </table>
        </div>
    )

    
}
