import { useMemo,useState,useEffect } from "react";
import { getAllrecords } from "../../Service/UserService";
import Table from "../../Component/Table/Table"
import axios from 'axios';


const Recordview = () =>{
    const columns = useMemo(() => [
        {
          Header: "id",
          accessor: 'id',
        },
        {
          Header: "First Name",
          accessor: 'firstname',
        },
        {
          Header: "Last Name",
          accessor: 'lastname',
        },
        {
          Header: "Begin Date",
          accessor: 'begindate',
        },
        {
          Header: "End Date",
          accessor: 'endate',
        },
        {
            Header: "Reason",
            accessor: 'message',
          }
      ], [])

    const record = [];
    var index =0;

    function breakdown(item) {
        record[index] = item;
        index++;
    }

    getAllrecords()
          .then(users => {
            users.map(breakdown)
          })
    
    // const retrieve = () => record;

    // const getData = () => {
    //     return record;
    //   }   

      const [data, setData] = useState([]);

      useEffect(() => {
        (async () => {
          const result = await axios("https://localhost:44327/Submit/get/records");
          setData(result.data);
        })();
      }, []);
      

    // const data = useMemo(()=> getData(), []);


    return(
        <div className="mt-5">
            <h2 className="text-center text-xl">Time off Chart</h2>
            <Table columns={columns} data={data} />
        </div>
    )

    

}
export default Recordview;
