import { getAllrecords } from "../../Service/UserService";
import Table from "../../Component/Table/Table"
import axios from 'axios';


const Recordview = () =>{
    const columns = useMemo(() => [
        {
          Header: "id",
          accessor: 'id',
        },
        {
          Header: "First Name",
          accessor: 'firstname',
        },
        {
          Header: "Last Name",
          accessor: 'lastname',
        },
        {
          Header: "Begin Date",
          accessor: 'begindate',
        },
        {
          Header: "End Date",
          accessor: 'endate',
        },
        {
            Header: "Reason",
            accessor: 'message',
          }
      ], [])

    // const record = [];
    
    // const retrieve = () => record;

